// Data Validation
frappe.ui.form.on("Supplier", "validate", function(frm) {
    if (frm.doc.supplier_registration_status!=="Approved") {
        frappe.msgprint(__("You can not create Supplier when 'Supplier Registration Application' not approved"));
        frappe.validated = false;
    }
});

// Date Validation 1
frappe.ui.form.on("Supplier", "refresh", function(frm) {
    if (frm.doc.date_of_valid_business_license < frappe.datetime.get_today() && frm.doc.disabled==0) {
        frappe.msgprint(__("Business License is expired"));
    }
});
// Date Validation 2
frappe.ui.form.on("Supplier", "refresh", function(frm) {
    if (frm.doc.date_of_valid_commercial_registry < frappe.datetime.get_today() && frm.doc.disabled==0) {
        frappe.msgprint(__("Commercial Registry is expired"));
    }
});
// Disabled supplier msg
frappe.ui.form.on("Supplier", "refresh", function(frm) {
    if (frm.doc.disabled==1) {
        frappe.msgprint(__("This Supplier is disabled"));
    }
});



// Data Validation 
frappe.ui.form.on("Supplier", "validate", function(frm) {
    if (frm.doc.contractor==0 && frm.doc.authorized_representative==0 && frm.doc.manufacturer==0 && frm.doc.trader==0) {
        frappe.msgprint(__("You shall select at least one choice from Nature of Business section"));
        frappe.validated = false;
    }
});
